mapper.select:
select

exists (

  select 1 from mutation

  where GENETIC_PROFILE_ID = #{geneticProfileId}

  and SAMPLE_ID = #{sampleId}

  and TUMOR_ALT_COUNT >= 0

  and TUMOR_REF_COUNT >= 0
)

select

mutation.ENTREZ_GENE_ID as entrezGeneId,
GROUP_CONCAT(mutation.SAMPLE_ID) as concatenatedSampleIds,
count(*) as count,
count(*)/`LENGTH` as count_per_nt

from mutation, gene

where mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID
and GENETIC_PROFILE_ID = #{geneticProfileId}


    and mutation.ENTREZ_GENE_ID in

        #{item}



    and mutation.SAMPLE_ID in

        #{item}



group by mutation.ENTREZ_GENE_ID


    having count(*) >= #{thresholdRecurrence}


order by count_per_nt desc


    LIMIT 0, #{thresholdNumGenes}

select
ENTREZ_GENE_ID as entrezGeneId, count(distinct SAMPLE_ID) as count
from mutation where GENETIC_PROFILE_ID = #{geneticProfileId}
and ENTREZ_GENE_ID in
#{item}
group by ENTREZ_GENE_ID

select
distinct cancer_study.CANCER_STUDY_IDENTIFIER as cancerStudyIdentifier,
count(patient.INTERNAL_ID) as count
count(patient.INTERNAL_ID) / (select count(sll.SAMPLE_ID) from sample_list_list sll, sample_list sl where sl.CATEGORY = 'all_cases_with_mutation_data' and sll.LIST_ID = sl.LIST_ID and sl.CANCER_STUDY_ID = cancer_study.CANCER_STUDY_ID) as frequency
from mutation_event, mutation, gene, sample, patient, cancer_study where gene.HUGO_GENE_SYMBOL = #{hugoGeneSymbol} and mutation_event.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID
and mutation_event.ONCOTATOR_PROTEIN_POS_START >= #{start}
and mutation_event.ONCOTATOR_PROTEIN_POS_END <= #{end}
and mutation_event.MUTATION_EVENT_ID = mutation.MUTATION_EVENT_ID and mutation.SAMPLE_ID = sample.INTERNAL_ID and sample.PATIENT_ID = patient.INTERNAL_ID and cancer_study.CANCER_STUDY_ID = patient.CANCER_STUDY_ID
and cancer_study.CANCER_STUDY_IDENTIFIER in
#{item}
group by patient.CANCER_STUDY_ID;
