mapper.select:
SELECT cna_event.ENTREZ_GENE_ID AS entrezGeneId, cna_event.ALTERATION AS alteration, COUNT(DISTINCT(sample_cna_event.SAMPLE_ID)) AS count FROM cna_event INNER JOIN sample_cna_event ON cna_event.CNA_EVENT_ID = sample_cna_event.CNA_EVENT_ID INNER JOIN genetic_profile ON sample_cna_event.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN sample ON sample_cna_event.SAMPLE_ID = sample.INTERNAL_ID WHERE genetic_profile.STABLE_ID = #{molecularProfileId}
AND (cna_event.ENTREZ_GENE_ID, cna_event.ALTERATION) IN
(#{entrezGeneIds[${i}]} , #{alterations[${i}]})
AND sample.STABLE_ID IN
#{item}
GROUP BY cna_event.ENTREZ_GENE_ID, cna_event.ALTERATION

SELECT cna_event.ENTREZ_GENE_ID AS entrezGeneId, cna_event.ALTERATION AS alteration, COUNT(DISTINCT(patient.INTERNAL_ID)) AS count FROM cna_event INNER JOIN sample_cna_event ON cna_event.CNA_EVENT_ID = sample_cna_event.CNA_EVENT_ID INNER JOIN genetic_profile ON sample_cna_event.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN sample ON sample_cna_event.SAMPLE_ID = sample.INTERNAL_ID INNER JOIN patient ON sample.PATIENT_ID = patient.INTERNAL_ID WHERE genetic_profile.STABLE_ID = #{molecularProfileId}
AND (cna_event.ENTREZ_GENE_ID, cna_event.ALTERATION) IN
(#{entrezGeneIds[${i}]},#{alterations[${i}]})
AND patient.STABLE_ID IN
#{item}
GROUP BY cna_event.ENTREZ_GENE_ID, cna_event.ALTERATION
