mapper.select:
SELECT cna_event.ENTREZ_GENE_ID AS entrezGeneId, gene.HUGO_GENE_SYMBOL AS hugoGeneSymbol, cna_event.ALTERATION AS alteration, COUNT(DISTINCT(sample_cna_event.SAMPLE_ID)) AS numberOfAlteredCases FROM cna_event INNER JOIN sample_cna_event ON cna_event.CNA_EVENT_ID = sample_cna_event.CNA_EVENT_ID INNER JOIN genetic_profile ON sample_cna_event.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN sample ON sample_cna_event.SAMPLE_ID = sample.INTERNAL_ID INNER JOIN gene ON cna_event.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID WHERE genetic_profile.STABLE_ID = #{molecularProfileId}
AND (cna_event.ENTREZ_GENE_ID, cna_event.ALTERATION) IN
(#{entrezGeneIds[${i}]}, #{alterations[${i}]})
AND sample.STABLE_ID IN
#{item}
GROUP BY cna_event.ENTREZ_GENE_ID, cna_event.ALTERATION
