mapper.select:
SELECT



    INNER JOIN mutation_event ON mutation.MUTATION_EVENT_ID = mutation_event.MUTATION_EVENT_ID


    INNER JOIN gene ON mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID



    ORDER BY ${sortBy} ${direction}


    ORDER BY genetic_profile.STABLE_ID ASC, sample.STABLE_ID ASC, mutation.ENTREZ_GENE_ID ASC


    LIMIT #{limit} OFFSET #{offset}

SELECT mutation.ENTREZ_GENE_ID AS entrezGeneId, COUNT(DISTINCT(mutation.SAMPLE_ID)) AS sampleCount FROM mutation INNER JOIN genetic_profile ON mutation.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID WHERE genetic_profile.STABLE_ID = #{geneticProfileId}
AND mutation.ENTREZ_GENE_ID IN
#{item}
GROUP BY mutation.ENTREZ_GENE_ID

SELECT mutation_event.KEYWORD AS keyword, COUNT(DISTINCT(mutation.SAMPLE_ID)) AS sampleCount FROM mutation INNER JOIN genetic_profile ON mutation.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN mutation_event ON mutation.MUTATION_EVENT_ID = mutation_event.MUTATION_EVENT_ID WHERE genetic_profile.STABLE_ID = #{geneticProfileId}
AND mutation_event.KEYWORD IN
#{item}
GROUP BY mutation_event.KEYWORD
