    INNER JOIN gene ON mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID



    ORDER BY ${sortBy} ${direction}


    ORDER BY genetic_profile.STABLE_ID ASC, sample.STABLE_ID ASC, mutation.ENTREZ_GENE_ID ASC


    LIMIT #{limit} OFFSET #{offset}

