)

mapper.select:
SELECT



    INNER JOIN mutation_event ON mutation.MUTATION_EVENT_ID = mutation_event.MUTATION_EVENT_ID


    INNER JOIN gene ON mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID



    ORDER BY ${sortBy} ${direction}


    ORDER BY genetic_profile.STABLE_ID ASC, sample.STABLE_ID ASC, mutation.ENTREZ_GENE_ID ASC


    LIMIT #{limit} OFFSET #{offset}

SELECT



    INNER JOIN mutation_event ON mutation.MUTATION_EVENT_ID = mutation_event.MUTATION_EVENT_ID


    INNER JOIN gene ON mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID



    ORDER BY ${sortBy} ${direction}


    ORDER BY genetic_profile.STABLE_ID ASC, sample.STABLE_ID ASC, mutation.ENTREZ_GENE_ID ASC


    LIMIT #{limit} OFFSET #{offset}

SELECT



    INNER JOIN mutation_event ON mutation.MUTATION_EVENT_ID = mutation_event.MUTATION_EVENT_ID


    INNER JOIN gene ON mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID



    ORDER BY ${sortBy} ${direction}


    ORDER BY genetic_profile.STABLE_ID ASC, sample.STABLE_ID ASC, mutation.ENTREZ_GENE_ID ASC


    LIMIT #{limit} OFFSET #{offset}

SELECT mutation.ENTREZ_GENE_ID AS entrezGeneId, gene.HUGO_GENE_SYMBOL AS hugoGeneSymbol, COUNT(*) AS totalCount, COUNT(DISTINCT(patient.INTERNAL_ID)) AS countByEntity FROM mutation INNER JOIN genetic_profile ON mutation.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN sample ON mutation.SAMPLE_ID = sample.INTERNAL_ID INNER JOIN patient ON sample.PATIENT_ID = patient.INTERNAL_ID INNER JOIN gene ON mutation.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID WHERE genetic_profile.STABLE_ID = #{molecularProfileId}
AND patient.STABLE_ID IN
#{item}
AND mutation.ENTREZ_GENE_ID IN
#{item}
AND mutation_event.REFERENCE_ALLELE IN ('A','T','C','G') AND mutation_event.TUMOR_SEQ_ALLELE IN ('A','T','C','G')
GROUP BY mutation.ENTREZ_GENE_ID

SELECT
#{entrezGeneId} AS entrezGeneId,
#{proteinPosStart} AS proteinPosStart,
#{proteinPosEnd} AS proteinPosEnd,
COUNT(*) AS count
FROM mutation
INNER JOIN mutation_event ON mutation.MUTATION_EVENT_ID = mutation_event.MUTATION_EVENT_ID
WHERE mutation_event.ENTREZ_GENE_ID = #{entrezGeneId}
AND mutation_event.ONCOTATOR_PROTEIN_POS_START >= #{proteinPosStart}
AND mutation_event.ONCOTATOR_PROTEIN_POS_END  <=  #{proteinPosEnd}
