SELECT
FROM structural_variant JOIN genetic_profile ON structural_variant.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID JOIN gene gene1 ON structural_variant.SITE1_ENTREZ_GENE_ID = gene1.ENTREZ_GENE_ID JOIN gene gene2 ON structural_variant.SITE2_ENTREZ_GENE_ID = gene2.ENTREZ_GENE_ID JOIN sample ON structural_variant.SAMPLE_ID = sample.INTERNAL_ID JOIN patient ON sample.PATIENT_ID = patient.INTERNAL_ID JOIN cancer_study ON patient.CANCER_STUDY_ID = cancer_study.CANCER_STUDY_ID AND genetic_profile.CANCER_STUDY_ID = cancer_study.CANCER_STUDY_ID
genetic_profile.STABLE_ID IN
#{item}
AND (structural_variant.SITE1_ENTREZ_GENE_ID in
#{item}
OR structural_variant.SITE2_ENTREZ_GENE_ID IN
#{item}
)
AND (genetic_profile.STABLE_ID, sample.STABLE_ID) IN
(#{molecularProfileIds[${i}]}, #{sampleIds[${i}]})
ORDER BY gene1.HUGO_GENE_SYMBOL, gene2.HUGO_GENE_SYMBOL
