mapper.select:
SELECT patient.STABLE_ID as patientId, clinical_event_data.VALUE as event, clinical_event.START_DATE as start, ifnull(clinical_event.STOP_DATE, clinical_event.START_DATE) as stop FROM clinical_event INNER JOIN clinical_event_data ON clinical_event.CLINICAL_EVENT_ID = clinical_event_data.CLINICAL_EVENT_ID INNER JOIN patient ON clinical_event.PATIENT_ID = patient.INTERNAL_ID INNER JOIN cancer_study ON patient.CANCER_STUDY_ID = cancer_study.CANCER_STUDY_ID
LEFT JOIN ( select GROUP_CONCAT(distinct sample.STABLE_ID) as sampleIds, patient.INTERNAL_ID from patient inner join sample on patient.INTERNAL_ID = sample.PATIENT_ID group by patient.INTERNAL_ID ) as p_samples ON p_samples.INTERNAL_ID = patient.INTERNAL_ID
WHERE cancer_study.CANCER_STUDY_IDENTIFIER IN
#{studyId}
AND clinical_event_data.KEY='AGENT' AND clinical_event_data.VALUE IN
#{eventValue}
