mapper.select:
WITH sample_counts as ( SELECT sample_list.cancer_study_id, countIf(stable_id LIKE '%_all') as allSampleCount FROM sample_list_list INNER JOIN sample_list ON sample_list_list.list_id = sample_list.list_id GROUP BY sample_list.cancer_study_id ) SELECT cs.cancer_study_id AS cancerStudyId, cs.cancer_study_identifier AS cancerStudyIdentifier, cs.type_of_cancer_id AS typeOfCancerId, cs.name AS name, cs.description AS description, cs.public AS publicStudy, cs.pmid AS pmid, cs.citation AS citation, cs.groups AS groups, cs.status AS status, cs.import_date AS importDate, reference_genome.name AS referenceGenome, allSampleCount, type_of_cancer.name AS type_of_cancer_name, type_of_cancer.dedicated_color AS type_of_cancer_dedicated_color, type_of_cancer.short_name AS type_of_cancer_short_name, type_of_cancer.parent AS type_of_cancer_parent
FROM cancer_study AS cs INNER JOIN reference_genome ON reference_genome.reference_genome_id = cs.reference_genome_id INNER JOIN type_of_cancer ON cs.type_of_cancer_id = type_of_cancer.type_of_cancer_id INNER JOIN sample_counts ON sample_counts.cancer_study_id = cs.cancer_study_id
cancer_study.cancer_study_identifier IN #{item}
cs.name ilike CONCAT('%', #{item}, '%') OR cs.cancer_study_identifier ilike CONCAT('%', #{item}, '%') OR type_of_cancer.name ilike CONCAT('%', #{item}, '%') OR type_of_cancer.type_of_cancer_id ilike CONCAT('%', #{item}, '%')
ORDER BY ${sortAndSearchCriteria.sortField} ${sortAndSearchCriteria.sortOrder}
ORDER BY cancer_study.name ASC
