mapper.select:
SELECT
where (sample.stable_id, mutation.entrez_gene_id, genetic_profile.stable_id) IN (select replaceOne(genomic_event_derived.sample_unique_id, concat(genomic_event_derived.cancer_study_identifier, '_'), ''), genomic_event_derived.entrez_gene_id, genomic_event_derived.genetic_profile_stable_id FROM genomic_event_derived
)

