sql.where:
replaceOne(genomic_event_derived.sample_unique_id, concat(genomic_event_derived.cancer_study_identifier, '_'), '') IN
#{sampleId}
AND
genomic_event_derived.genetic_profile_stable_id IN
#{profileId}
AND
genomic_event_derived.entrez_gene_id IN
#{geneId}
-- Extra guard to protect retrieving other types of event from derived table AND endsWith(genetic_profile_stable_id, '_mutations')
