mapper.select:
SELECT sl.LIST_ID, cs.CANCER_STUDY_IDENTIFIER as cancerStudyIdentifier, sl.STABLE_ID as stableId, sl.NAME as name, sl.DESCRIPTION as description, s.STABLE_ID as sampleId FROM sample_list sl JOIN cancer_study cs ON cs.CANCER_STUDY_ID = sl.CANCER_STUDY_ID JOIN sample_list_list sll ON sll.LIST_ID = sl.LIST_ID JOIN sample s ON s.INTERNAL_ID = sll.SAMPLE_ID
JOIN ( SELECT * FROM (VALUES
ROW(#{sid})
) AS temp(sample_id) ) AS sample_ids_subquery ON sample_ids_subquery.sample_id = s.STABLE_ID
WHERE cs.CANCER_STUDY_IDENTIFIER = #{studyId}
