WITH ids_only AS ( SELECT DISTINCT p.INTERNAL_ID AS PATIENT_INTERNAL_ID, p.STABLE_ID AS patientId FROM patient p
JOIN sample s ON s.PATIENT_ID = p.INTERNAL_ID JOIN ( SELECT * FROM (VALUES
ROW(#{sid})
) AS temp(sample_id) ) AS sample_ids_subquery ON sample_ids_subquery.sample_id = s.STABLE_ID
JOIN cancer_study cs ON cs.CANCER_STUDY_ID = p.CANCER_STUDY_ID WHERE cs.CANCER_STUDY_IDENTIFIER = #{studyId} ) ( SELECT PATIENT_INTERNAL_ID AS rowKey, 'PATIENT_ID' AS attributeId, patientId AS attributeValue FROM ids_only ) UNION ALL ( SELECT ids_only.PATIENT_INTERNAL_ID AS rowKey, cpa.ATTR_ID AS attributeId, cpa.ATTR_VALUE AS attributeValue FROM ids_only JOIN clinical_patient cpa ON cpa.INTERNAL_ID = ids_only.PATIENT_INTERNAL_ID WHERE cpa.ATTR_ID NOT IN (
) ) ORDER BY rowKey

SELECT DISTINCT ced.CLINICAL_EVENT_ID as clinicalEventId, ced.`KEY` as `key`, ced.VALUE as value FROM clinical_event ce JOIN clinical_event_data ced ON ced.CLINICAL_EVENT_ID = ce.CLINICAL_EVENT_ID JOIN patient p ON p.INTERNAL_ID = ce.PATIENT_ID
JOIN sample s ON s.PATIENT_ID = p.INTERNAL_ID JOIN ( SELECT * FROM (VALUES
ROW(#{sid})
) AS temp(sample_id) ) AS sample_ids_subquery ON sample_ids_subquery.sample_id = s.STABLE_ID
JOIN cancer_study cs ON cs.CANCER_STUDY_ID = p.CANCER_STUDY_ID WHERE cs.CANCER_STUDY_IDENTIFIER = #{studyId}
AND BINARY ce.EVENT_TYPE = #{eventType}
ORDER BY ced.CLINICAL_EVENT_ID

SELECT DISTINCT ce.CLINICAL_EVENT_ID as clinicalEventId, p.STABLE_ID AS patientId, ce.START_DATE as startDate, ce.STOP_DATE as stopDate, ce.EVENT_TYPE as eventType FROM clinical_event ce JOIN patient p ON p.INTERNAL_ID = ce.PATIENT_ID
JOIN sample s ON s.PATIENT_ID = p.INTERNAL_ID JOIN ( SELECT * FROM (VALUES
ROW(#{sid})
) AS temp(sample_id) ) AS sample_ids_subquery ON sample_ids_subquery.sample_id = s.STABLE_ID
JOIN cancer_study cs ON cs.CANCER_STUDY_ID = p.CANCER_STUDY_ID WHERE cs.CANCER_STUDY_IDENTIFIER = #{studyId}
AND BINARY ce.EVENT_TYPE = #{eventType}
ORDER BY ce.CLINICAL_EVENT_ID
