mapper.select:
SELECT

FROM gene_panel

    ORDER BY "${sortBy}" ${direction}


    ORDER BY gene_panel.stable_id ASC


    LIMIT #{limit} OFFSET #{offset}

SELECT gene_panel.stable_id AS genePanelId, gene_panel_list.gene_id AS entrezGeneId, gene.hugo_gene_symbol AS hugoGeneSymbol FROM gene_panel_list INNER JOIN gene_panel ON gene_panel_list.internal_id = gene_panel.internal_id INNER JOIN gene ON gene_panel_list.gene_id = gene.entrez_gene_id
gene_panel.stable_id IN #{item}
ORDER BY hugoGeneSymbol ASC

SELECT


INNER JOIN sample_list_list as sample_list_list_query ON sample_list_list_query.sample_id = sample.internal_id
