mapper.select:
SELECT gene.entrez_gene_id AS "entrezGeneId", genetic_alteration.`values` AS "values"
,
FROM genetic_alteration INNER JOIN genetic_profile ON genetic_alteration.genetic_profile_id = genetic_profile.genetic_profile_id INNER JOIN gene ON genetic_alteration.genetic_entity_id = gene.genetic_entity_id
genetic_profile.stable_id = #{molecularProfileId}
AND gene.entrez_gene_id IN #{item}

SELECT gene.entrez_gene_id AS "entrezGeneId", genetic_alteration.`values` AS "values"
,
FROM genetic_alteration INNER JOIN genetic_profile ON genetic_alteration.genetic_profile_id = genetic_profile.genetic_profile_id INNER JOIN gene ON genetic_alteration.genetic_entity_id = gene.genetic_entity_id
genetic_profile.stable_id = #{molecularProfileId}
AND gene.entrez_gene_id IN #{item}

SELECT geneset.external_id AS genesetId,
NULL AS "values"
genetic_alteration.`values` AS "values"
FROM genetic_alteration INNER JOIN genetic_profile ON genetic_alteration.genetic_profile_id = genetic_profile.genetic_profile_id INNER JOIN geneset ON genetic_alteration.genetic_entity_id = geneset.genetic_entity_id
genetic_profile.stable_id = #{molecularProfileId}
AND geneset.external_id IN #{item}

SELECT genetic_entity.stable_id AS genericAssayStableId, genetic_profile.stable_id AS molecularProfileId,
NULL AS "values"
genetic_alteration.`values` AS "values"
FROM genetic_alteration INNER JOIN genetic_profile ON genetic_alteration.genetic_profile_id = genetic_profile.genetic_profile_id INNER JOIN genetic_entity ON genetic_alteration.genetic_entity_id = genetic_entity.id
genetic_profile.stable_id = #{molecularProfileId}
AND genetic_entity.stable_id IN #{item}

