mapper.select:
SELECT DISTINCT JSON_UNQUOTE(outerKey) AS outerKey
FROM (



) combinedAnnotations,
json_table(json_keys(annotation_json), '$[*]' COLUMNS(outerKey JSON PATH '$')) t;

SELECT DISTINCT JSON_UNQUOTE(innerKey) AS innerKey, #{outerKey} AS outerKey
FROM (



) combinedAnnotations,
json_table(json_keys(JSONExtractRaw(annotation_json, concat('$.', #{outerKey}))),
'$[*]' COLUMNS(innerKey JSON PATH '$')) t;

SELECT DISTINCT(JSONExtractString(annotation_json, concat('$.', #{outerKey}, '.', #{innerKey})))) AS attrValue, #{outerKey} AS outerKey, #{innerKey} AS innerKey, studyId AS studyId, sampleId AS sampleId FROM ( SELECT annotation_json, sample.stable_id AS sampleId, cancer_study.cancer_study_identifier AS studyId FROM mutation
UNION ALL
SELECT annotation_json, sample.stable_id AS sampleId, cancer_study.cancer_study_identifier AS studyId FROM structural_variant
UNION ALL
SELECT annotation_json, sample.stable_id AS sampleId, cancer_study.cancer_study_identifier AS studyId FROM sample_cna_event
) combinedNamespaceData WHERE JSONExtractRaw(annotation_json, concat('$.', #{outerKey}, '.', #{innerKey})) IS NOT NULL;

SELECT #{outerKey} AS outerKey, #{value} AS attrValue, #{innerKey} AS innerKey, studyId, sampleId, patientId FROM ( SELECT sample.stable_id AS sampleId, patient.stable_id AS patientId, cancer_study.cancer_study_identifier AS studyId FROM mutation
AND JSONExtractString(annotation_json, concat('$.', #{outerKey}, '.', #{innerKey}))) = #{value}
UNION ALL
SELECT sample.stable_id AS sampleId, patient.stable_id AS patientId, cancer_study.cancer_study_identifier AS studyId FROM structural_variant
AND JSONExtractString(annotation_json, concat('$.', #{outerKey}, '.', #{innerKey}))) = #{value}
UNION ALL
SELECT sample.stable_id AS sampleId, patient.stable_id AS patientId, cancer_study.cancer_study_identifier AS studyId FROM sample_cna_event
AND JSONExtractString(annotation_json, concat('$.', #{outerKey}, '.', #{innerKey}))) = #{value} ) combinedNamespaceData GROUP BY sampleId, studyId, patientId;
