mapper.select:
SELECT



FROM clinical_attribute_meta
INNER JOIN cancer_study ON clinical_attribute_meta.cancer_study_id = cancer_study.cancer_study_id


        cancer_study.cancer_study_identifier IN
            #{item}



    ORDER BY "${sortBy}" ${direction}


    ORDER BY clinical_attribute_meta.attr_id ASC


    LIMIT #{limit} OFFSET #{offset}

SELECT



FROM clinical_attribute_meta
INNER JOIN cancer_study ON clinical_attribute_meta.cancer_study_id = cancer_study.cancer_study_id
WHERE clinical_attribute_meta.attr_id = #{clinicalAttributeId}
AND cancer_study.cancer_study_identifier = #{studyId}

SELECT clinical_attribute_meta.attr_id AS "attrId", cancer_study.cancer_study_identifier AS "cancerStudyIdentifier", clinical_attribute_meta.cancer_study_id AS "cancerStudyId", clinical_attribute_meta.patient_attribute AS "patientAttribute", clinical_attribute_meta.display_name AS "displayName", clinical_attribute_meta.datatype AS "datatype" FROM clinical_attribute_meta INNER JOIN cancer_study ON clinical_attribute_meta.cancer_study_id = cancer_study.cancer_study_id
cancer_study.cancer_study_identifier IN #{item}
AND clinical_attribute_meta.attr_id IN #{item}
