mapper.select:
SELECT hugo_gene_symbol as hugoGeneSymbol, 1 as entrezGeneId, COUNT(DISTINCT sample_unique_id) as numberOfAlteredCases, COUNT(*) as totalCount FROM genomic_event_derived
variant_type = 'mutation' AND
GROUP BY hugo_gene_symbol;

SELECT hugo_gene_symbol as hugoGeneSymbol, 1 as entrezGeneId, cna_alteration as alteration, cna_cytoband as cytoband, COUNT(DISTINCT sample_unique_id) as numberOfAlteredCases, COUNT(*) as totalCount FROM genomic_event_derived
variant_type = 'cna' AND
GROUP BY hugo_gene_symbol, alteration, cytoband;

SELECT hugo_gene_symbol as hugoGeneSymbol, 1 as entrezGeneId, COUNT(DISTINCT sample_unique_id) as numberOfAlteredCases, COUNT(*) as totalCount FROM genomic_event_derived
variant_type = 'structural_variant' AND
GROUP BY hugo_gene_symbol;

--we need to derive the alteration type from the stable_id by removing cancer study id --this should probaby be refactored at some point but we need to maintain api interface SELECT replaceOne(genetic_profile.stable_id, concat(sample_derived.cancer_study_identifier,'_'), '') AS value, genetic_profile.stable_id, genetic_profile.name AS label, count(sample_profile.genetic_profile_id) AS count FROM sample_profile LEFT JOIN sample_derived ON sample_profile.sample_id=sample_derived.internal_id LEFT JOIN genetic_profile on sample_profile.genetic_profile_id = genetic_profile.genetic_profile_id
sample_unique_id IN ( )
GROUP BY genetic_profile.stable_id, genetic_profile.name, sample_derived.cancer_study_identifier;

SELECT name AS label, REPLACE(stable_id, CONCAT(cancer_study_identifier, '_'), '') AS value, count(sample_id) AS count FROM sample_list_list sll LEFT JOIN sample_mv s ON sll.sample_id=s.internal_id LEFT JOIN sample_list sl on sll.list_id=sl.list_id
sample_unique_id IN ( )
GROUP BY s.cancer_study_identifier, sl.stable_id, sl.name;

mapper.sql:
SELECT attribute_name as attributeId,
, count(value) as count FROM clinical_data_derived
type='sample' AND sample_unique_id IN (
INTERSECT SELECT sample_unique_id from sample_derived WHERE sample_derived.patient_unique_id IN ()
)
AND UPPER(value) NOT IN
#{filteredAttributeValue}
AND attribute_name IN
#{attributeId}
GROUP BY attribute_name, value

